import React from 'react';

const defaultData = [
  { title: 'Card 1', description: 'Description for Card 1.' },
  { title: 'Card 2', description: 'Description for Card 2.' },
  // Add more default data objects here
];

const CardComponent = ({ data = defaultData, maxCards = defaultData.length }) => {
  // Limit the number of cards to the maximum specified
  const limitedData = data.slice(0, maxCards);
  while (data.length > 6) {
    data.shift(); // Remove the first element if the array length is greater than 6
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
      {limitedData.map((item, index) => (
        <div key={index} style={{ width: '50%' }}>
          {/* Render each card */}
          <div style={{ margin: '10px', border: '1px solid black', padding: '10px' }}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
