import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './OnBoarding.css';

function OnBoarding() {
  const [screen, setScreen] = useState(1);
  const [fade, setFade] = useState(true);
  const [text, setText] = useState("");
  const [textFade, setTextFade] = useState(true);

  const renderProgressBar = () => {
    return (
      <div className="progress-bar">
        <img src="/Assets/onboarding_1.png" alt="Step 1" className={screen === 1 ? 'active' : ''} />
        <img src="/Assets/onboarding_2.png" alt="Step 2" className={screen === 2 ? 'active' : ''} />
        <img src="/Assets/onboarding_3.png" alt="Step 3" className={screen === 3 ? 'active' : ''} />
        <img src="/Assets/onboarding_4.png" alt="Step 4" className={screen === 4 ? 'active' : ''} />
      </div>
    );
  };

  const nextScreen = () => {
    setFade(false); // Trigger fade out

    setTimeout(() => {
      setScreen(screen => (screen === 4 ? 1 : screen + 1));
      setFade(true); // Trigger fade in
    }, 500); // This duration should match the CSS transition duration
  };

  const changeText = (newText) => {
    setTextFade(false);
    setTimeout(() => {
      setText(newText);
      setTextFade(true);
    }, 500); // match this with your fade animation duration
  };

  useEffect(() => {
    setFade(true);
  }, []);

  const renderScreen = () => {
    return (
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {(() => {
          switch (screen) {
            case 1:
              return (
                <div className="center-container">
                  <img src="/Assets/intro.png" alt="Your Image" className="center-image" />
                  <div className={`textbox ${textFade ? 'fade-in' : 'fade-out'}`}>
                    <p>{text}</p>
                  </div>
                  <div className="button-group">
                    <button onClick={() => changeText("🌟 Your Personalized Engagement Hub\n\nStep into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.")}></button>
                    <button onClick={() => changeText("🤖 AI-Powered Drafts, Uniquely You\n\nSuperpowerDM isnt just smart; its intuitive. Our advanced language model trains to match your unique communication style, drafting replies that sound authentically you.")}></button>
                    <button onClick={() => changeText("🎨 Crafted Replies, Curated by You\n\nYour voice, your rules. Set the tone and let SuperpowerDM propose responses that echo your personal touch. Review, tweak, and send messages that truly represent your brand.")}></button>
                    <button onClick={() => changeText("👥 Engage with Precision\n\nTarget the comments that align with your brands vision. Whether its a heartfelt thank you or a witty reply to a question, your drafts are ready for your magic touch.")}></button>
                    <button onClick={() => changeText("🛠️ Tailor Your Engagement Strategy\n\nCustomize the criteria for your automated replies—whether its engaging your most loyal followers or acknowledging the early birds. Your SuperpowerDM learns and adapts to your preferences.")}></button>
                    <button onClick={() => changeText("🌱 Nurture Your Digital Ecosystem\n\nYour content is the seed of your community. With SuperpowerDM, you have the power to cultivate lasting relationships, one personalized message at a time.")}></button>
                    <button onClick={nextScreen}>Go to Next Screen</button>
                    {/* More buttons */}
                  </div>
                </div>
              );
            // ... cases for other screens
            default:
              return null;
          }
        })()}
      </div>
    );
  };

  return (
    <div className="onboarding">
      {renderProgressBar()}
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {renderScreen(screen)}
      </div>
      <img className='bottom-img' src="/Assets/blackFullLogo.png" alt="Superpower Logo" />
    </div>
  );
}

export default OnBoarding;
