import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

function YourNewPage() {
  const [screen, setScreen] = useState(1);
  const [fade, setFade] = useState(true);

  const nextScreen = () => {
    setFade(false); // Trigger fade out

    setTimeout(() => {
      setScreen(screen => (screen === 6 ? 1 : screen + 1)); // Update for 6 screens
      setFade(true); // Trigger fade in
    }, 500); // This duration should match the CSS transition duration
  };

  const renderScreen = () => {
    return (
      (() => {
        switch (screen) {
          case 1:
            return <div className='content'> {/* Content for Screen 1 */} </div>;
          case 2:
            return <div className='content'> {/* Content for Screen 2 */} </div>;
          case 3:
            return <div className='content'> {/* Content for Screen 3 */} </div>;
          case 4:
            return <div className='content'> {/* Content for Screen 4 */} </div>;
          case 5:
            return <div className='content'> {/* Content for Screen 5 */} </div>;
          case 6:
            return <div className='content'> {/* Content for Screen 6 */} </div>;
          default:
            return <div>Something went wrong!</div>;
        }
      })()
    );
  };

  return (
    <div className="your-new-page">
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {renderScreen()}
      </div>
      <button onClick={nextScreen}>Next Screen</button> {/* Button to navigate to the next screen */}
    </div>
  );
}

export default YourNewPage;
