import React from 'react';

const CardComponent = ({ data, maxCards }) => {
  // Calculate the maximum number of rows
  const maxRows = Math.ceil(maxCards / 2);

  // Limit the number of cards to the maximum specified
  const limitedData = data.slice(0, maxRows * 2);

  return (
    <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
      {limitedData.map((item, index) => (
        <div key={index} style={{ width: '50%' }}>
          <div style={{ margin: '10px', border: '1px solid black', padding: '10px' }}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
