import React, { useState } from 'react';

import './Login.css'; // We will create this CSS file next

function LoginScreen() {

  const [showLogin, setShowLogin] = useState(true);

  function handleLoginClick() {
    setShowLogin(false);
    setTimeout(() => {
      setShowLogin(true);
    }, 500); 
  }  

  return (
    <div className={`login-screen ${showLogin ? '' : 'fade-out'}`}>
      {showLogin ? (
        <>
          <img src="Assets/colourFullLogo.png" alt="Logo" className="centered-image" />
          <h2>Helping Creators Maximize Follower-Engagement with AI</h2>
          <button className="login-button" onClick={handleLoginClick}>Log In</button>
          <button className="login-button" onClick={() => window.open('https://yourwebsite.com', '_blank')}>
            Request Access
          </button>
          {/* Other content */}
        </>
      ) : (
        <div>New Content Here</div>
      )}
    </div>
  );
}

export default LoginScreen;
