import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

function YourNewPage() {
  const [currentScreen, setCurrentScreen] = useState(1);
  const [fade, setFade] = useState(true);
  const [expanded, setExpanded] = useState(false);

  const changeScreen = (newScreen) => {
    setFade(false);

    setTimeout(() => {
      setCurrentScreen(newScreen); // Set the new screen number
      setFade(true); 
    }, 500); // Adjust the timeout duration as needed
  };


  const renderScreen = () => {
    switch (currentScreen) { // Make sure to use currentScreen instead of screen
      case 1:
        return (
          <div className='content'>
            <p> Control Centre </p> 
          </div>
        );
      case 2:
        return (
          <div className='content'>
            <p> Process Configuration </p>
          </div>
        );
        case 3:
        return (
          <div className='content'>
            <p> Settings </p>
          </div>
        );
      // ... and so on for the other cases
      default:
        return <div>Something went wrong!</div>;
    }
  };

  return (
    <div className="Background">
      
      
      <div className="Sidebar">
        <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
          <div 
              className={`sidebar ${expanded ? 'expanded' : ''}`}
              onMouseEnter={() => setExpanded(true)}
              onMouseLeave={() => setExpanded(false)}
          >
            <button onClick={() => changeScreen(1)}>Control Centre</button>
          </div>
          {renderScreen()}
        </div>
      </div>
    </div>
  );
}

export default YourNewPage;
