  const [activeButton, setActiveButton] = useState(1);

  const text1 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text2 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text3 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text4 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text5 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text6 = ['ðŸŒŸ Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];

  const changeText = (newText, buttonNumber) => {
    setCurrentText(newText);
    setActiveButton(buttonNumber);
  };

  return (
    <div className="screen">
      <img className="top-image" src="/Assets/onboarding_1.png" alt="Top" />
      <div className="center-content">
        <img className="middle-image" src="/Assets/intro.png" alt="Middle" />
        <p className="center-text">{currentText}</p>
        <div className="button-group">
          <button className={activeButton === 1 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text1, 1)}></button>
          <button className={activeButton === 2 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text2, 2)}></button>
          <button className={activeButton === 3 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text3, 3)}></button>
          <button className={activeButton === 4 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text4, 4)}></button>
          <button className={activeButton === 5 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text5, 5)}></button>
          <button className={activeButton === 6 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text6, 6)}></button>
        </div>
      </div>
      <img className="bottom-image" src="/Assets/whiteFullLogo.png" alt="Bottom" />
    </div>
  );
}

export default OnBoarding;
