import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

function YourNewPage() {
  const [currentScreen, setCurrentScreen] = useState(1);
  const [fade, setFade] = useState(true);
  const [expanded, setExpanded] = useState(false);

  const changeScreen = (newScreen) => {
    setTimeout(() => {
      setCurrentScreen(newScreen); // Set the new screen number
    }, 100); // Adjust the timeout duration as needed
  };


  const renderScreen = () => {
    switch (currentScreen) { // Make sure to use currentScreen instead of screen
      case 1:
        return (
          <div className='content'>
            <div className="max-w-lg mx-auto my-10 p-6 bg-white shadow-lg rounded-lg">
              <div className="flex justify-between items-center mb-4">
                <div className="flex items-center">
                  <span className="text-3xl font-semibold mr-2">0/23</span>
                  <span className="text-xl font-medium">Comments replied</span>
                </div>
                <button className="p-2 bg-gray-200 rounded-full focus:outline-none hover:bg-gray-300 transition">
                  {/* SVG code here */}
                </button>
              </div>
              <div className="mb-6">
                <img src="https://source.unsplash.com/random/400x200" alt="Random Image" className="rounded-lg mb-4" />
                <div className="text-lg font-medium mb-2">Post date: November 12, 12pm EST</div>
                <button className="bg-green-500 text-white px-6 py-2 rounded-lg font-semibold hover:bg-green-600 transition">
                  Review 23 draft replies
                  {/* SVG code here */}
                </button>
              </div>
            </div>
          </div>
        );
      case 2:
        return (
          <div className='content'>
            <p> Process Configuration </p>
          </div>
        );
        case 3:
        return (
          <div className='content'>
            <p> Settings </p>
          </div>
        );
        case 4:
        return (
          <div className='content'>
            <p> Coming Soon! </p>
          </div>
        );
      // ... and so on for the other cases
      default:
        return <div>Something went wrong!</div>;
    }
  };

  return (
    <div className="Background">
      {renderScreen()}
      <div className={`sidebar ${expanded ? 'expanded' : ''}`} 
           onMouseEnter={() => setExpanded(true)} 
           onMouseLeave={() => setExpanded(false)} >
        {expanded ? (
          <>
            <div onClick={() => changeScreen(1)}>
              <img src="/Assets/colourFullLogo.png" alt="Logo" className='side-logo' />
            </div>
            <button onClick={() => changeScreen(1)}> 
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Control Centre
            </button>
            <button onClick={() => changeScreen(2)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Process Configuration
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Campaigns (~Coming Soon!)
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Audiences (~Coming Soon!)
            </button>
            <button onClick={() => changeScreen(3)} className="button-settings"> 
              <img src="/Assets/SettingsIcon.png" alt="Icon" className="button-icon" />
              Settings
            </button>
          </>
        ) : (
          <>
            <div onClick={() => changeScreen(1)}>
              <img src="/Assets/redIcon.png" alt="Logo" className='side-logo-closed' />
            </div>
            <button onClick={() => changeScreen(1)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(2)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(3)} className="button-settings"> 
              <img src="/Assets/SettingsIcon.png" alt="Icon" className="button-icon" />
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default YourNewPage;
