import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

const YourComponent = () => {
  const [currentScreen, setCurrentScreen] = useState(1);

  const changeScreen = (screenNumber) => {
    setCurrentScreen(screenNumber);
  };

  return (
    <div>
      {/* Buttons to change screens */}
      <button onClick={() => changeScreen(1)}>Screen 1</button>
      <button onClick={() => changeScreen(2)}>Screen 2</button>
      <button onClick={() => changeScreen(3)}>Screen 3</button>

      {/* Screens with conditional rendering */}
      <div className={`screen ${currentScreen === 1 ? 'visible' : 'hidden'}`}>
        Screen 1
      </div>
      <div className={`screen ${currentScreen === 2 ? 'visible' : 'hidden'}`}>
        Screen 2
      </div>
      <div className={`screen ${currentScreen === 3 ? 'visible' : 'hidden'}`}>
        Screen 3
      </div>
    </div>
  );
};

export default YourNewPage;
