import React, { useState } from 'react';
import './OnBoarding.css'; // Make sure to create this CSS file


function OnBoarding() {
  const [currentText, setCurrentText] = useState('Your custom dashboard is where engagement with your followers meets efficiency.');

  const text1 = 'Your custom dashboard is where engagement with your followers meets efficiency.';
  const text2 = 'New text for button 2.';
  const text3 = 'New text for button 3.';

  const changeText = (newText) => {
    setCurrentText(newText);
  };

  return (
    <div className="screen">
      <img className="top-image" src="/Assets/onboarding_1.png" alt="Top" />
      <div className="center-content">
        <img className="middle-image" src="/Assets/intro.png" alt="Middle" />
        <p className="center-text">Your custom dashboard is where engagement with your followers meets efficiency. <br />
        <br /> Monitor the pulse of your posts and steer your community interactions with ease.</p>
      </div>
      <img className="bottom-image" src="/Assets/whiteFullLogo.png" alt="Bottom" />
    </div>
  );
}

export default OnBoarding;
