import React from 'react';

const defaultData = [
  { title: 'Card 1', description: 'Description for Card 1.' },
  { title: 'Card 2', description: 'Description for Card 2.' },
  // Add more default data objects here
];

const CardComponent = ({ data = defaultData, maxCards = defaultData.length }) => {
  // Limit the number of cards to the maximum specified
  const limitedData = data.slice(0, maxCards);
  while (data.length > 6) {
    data.shift();
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
      {data.map((item, index) => (
        <div key={index} style={{ width: '50%', boxSizing: 'border-box', padding: '10px' }}>
          {/* Card Header */}
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
            <span>{index + 1}/{data.length} comments replied to</span>
            <button style={{ borderRadius: '50%', width: '30px', height: '30px' }}>&#9679;</button>
          </div>

          {/* Card Content */}
          <div style={{ display: 'flex', border: '1px solid black', padding: '10px' }}>
            {/* Image on the left */}
            <img src={item.imageUrl} alt="Post" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />

            {/* Text and Button on the right */}
            <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
              <div>
                <h4>Post Date</h4>
                <p>{item.date}</p>
              </div>
              <button>View Post</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
