import React, { useState } from 'react';

// const defaultData = [];

const defaultData = [
  {
    id: '18068242561455354',
    imageUrl: 'https://picsum.photos/id/237/1080/1080',
    date: '2023-12-09T21:35:36+0000',
    username: 'user1',
    comments: [
      { username: 'commenter1', text: 'Great post!' },
      { username: 'commenter2', text: 'Love this!' }
    ]
  },
  {
    id: '17988767471580842',
    imageUrl: 'https://picsum.photos/id/7/1080/1920',
    date: '2023-12-09T21:32:54+0000',
    username: 'user2',
    comments: [
      { username: 'commenter3', text: 'Amazing content!' },
      { username: 'commenter4', text: 'Really nice picture.' }
    ]
  },
  {
    id: '17906410409793067',
    imageUrl: 'https://picsum.photos/id/63/1080/1080',
    date: '2023-11-16T14:51:08+0000',
    username: 'user3',
    comments: [
      { username: 'commenter5', text: 'This is awesome!' }
    ]
  },
  {
    id: '17987706869244506',
    imageUrl: 'https://picsum.photos/id/16/1080/1920',
    date: '2023-11-03T16:16:05+0000',
    username: 'user4',
    comments: [
      { username: 'commenter6', text: 'I like this post a lot.' },
      { username: 'commenter7', text: 'Great job!' },
      { username: 'commenter8', text: 'Very cool!' }
    ]
  },
  {
    id: '18233445566789012',
    imageUrl: 'https://picsum.photos/id/5/1080/1080',
    date: '2023-10-25T10:30:21+0000',
    username: 'user5',
    comments: [
      { username: 'commenter9', text: 'Superb!' },
      { username: 'commenter10', text: 'Inspiring work.' }
    ]
  },
  {
    id: '17654367890987654',
    imageUrl: 'https://picsum.photos/id/59/1080/1080',
    date: '2023-10-20T08:15:45+0000',
    username: 'user6',
    comments: [
      { username: 'commenter11', text: 'Fantastic!' },
      { username: 'commenter12', text: 'Wow, this is great!' }
    ]
  }
];


const CardComponent = ({maxCards}) => {
  const [data, setData] = useState(() => {
    const savedData = localStorage.getItem('defaultData');
    return savedData ? JSON.parse(savedData) : [];
  });

  const [displayedComments, setDisplayedComments] = useState({});
  const [activeCard, setActiveCard] = useState(null);

  const fetchMedia = async () => {
    try {
      const response = await fetch('/fetch_media', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const jsonData = await response.json();
      const transformedData = jsonData.map(item => ({
        imageUrl: item.media_url,
        date: item.timestamp,
        id: item.id,
        username: item.username,
        comments: [] // Initialize comments as an empty array
      }));

      // Update state and save to localStorage
      setData(prevData => {
        const updatedData = [...prevData, ...transformedData];
        localStorage.setItem('defaultData', JSON.stringify(updatedData));
        return updatedData;
      });
    } catch (error) {
      console.error('Error fetching media:', error);
    }
  };

  const fetchComments = async (mediaId, username) => {
    try {
      const response = await fetch('/fetch_comments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ media_id: mediaId, username: username })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const comments = await response.json();
      setDisplayedComments({ [mediaId]: comments });
      setData(prevData => {
        return prevData.map(item => {
          if (item.id === mediaId) {
            return { ...item, comments: comments };
          }
          return item;
        });
      });

    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleCardButtonClick = (card) => {
    setActiveCard(card); // Set the active card
  };
  const closeDetailScreen = () => {
    setActiveCard(null); // Reset the active card
  };
  
  // Check if data is empty, and if so, use defaultData
  const effectiveData = data.length === 0 ? defaultData : data;
  // Determine the number of cards to display
  const numCardsToShow = maxCards || effectiveData.length;
  // Slice the data to limit the number of cards if necessary
  const limitedData = effectiveData.slice(0, numCardsToShow);

  const renderDetailScreen = () => (
    <div className="detail-screen">
      <h2>{activeCard.username}'s Post</h2>
      <img src={activeCard.imageUrl} alt="Post" />
      <p>Date: {activeCard.date}</p>
      <h3>Comments:</h3>
      {activeCard.comments.map((comment, index) => (
        <p key={index}><strong>{comment.username}:</strong> {comment.text}</p>
      ))}
      <button onClick={closeDetailScreen}>Close</button>
    </div>
  );

  return (
    <div>
      {activeCard ? (
        renderDetailScreen()
      ) : (
      <div className="card-container">
      <div className="refresh-button">
          <button onClick={fetchMedia}>
          <span> Refresh feed </span>
          <img src="/Assets/refresh_red.png" className="reload" />
          </button>
          <div className="tooltip">
                  <span className="tooltip-icon">?</span>
                  <span className="tooltip-text">A process is a set of structured activities and tasks performed to achieve a specific goal.</span>
          </div>
      </div>
        {limitedData.length > 0 ? limitedData.map((item, index) => (
          <div key={item.id || index} className="card">
            {/* Card Header */}
            <div className="card-header">
            <span className="counter-box">{item.comments?.length || 0}</span>
              <h2> Comments replied </h2>
              <button className="circle-button" onClick={() => fetchComments(item.id, item.username)}>
                <img src="/Assets/reload.png" className="reload" />
              </button>
            </div>
            {/* Card Content */}
            <div className="card-content">
              {/* Image on the left */}
              <img src={item.imageUrl} alt="Post" className="card-image" />

              {/* Text and Button on the right */}
              <div className="card-text">
                <div>
                  <h4>Post Date:</h4>
                  <p className="card-date">{item.date}</p>
                </div>
                <button className="card-button" onClick={() => handleCardButtonClick(item)}>
                    Review {item.comments.length} Draft Replies >
                </button>
              </div>
            </div>
          </div>
        )) : (
          <div className="empty-screen">
            <h1>No content!</h1>
            <p>Your content will appear here when available.</p>
            <img src="path_to_your_image.jpg" alt="Descriptive Alt Text" />
            <button onClick={fetchMedia}>Get Content</button>
          </div>
        )}
      </div>
      )}
    </div>
  );
};

export default CardComponent;
