import React from 'react';

const fetchMedia = async () => {
  try {
      const response = await fetch('/fetch_media', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          }
          // If you need to send a body, use:
          // body: JSON.stringify({ key: 'value' })
      });

      if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log(data); // Handle your data here
  } catch (error) {
      console.error('Error fetching media:', error);
  }
};

const defaultData = [
    { imageUrl: 'https://picsum.photos/id/237/200/300', date: '2023-12-08' },
    { imageUrl: 'https://picsum.photos/id/257/200/300', date: '2023-12-07' },
    { imageUrl: 'https://picsum.photos/id/217/200/300', date: '2023-12-07' },
    { imageUrl: 'https://picsum.photos/id/137/200/300', date: '2023-12-07' },
    { imageUrl: 'https://picsum.photos/id/1/200/300', date: '2023-12-07' },
  ];

const CardComponent = ({ data = defaultData, maxCards = defaultData.length }) => {
  // Limit the number of cards to the maximum specified
  const limitedData = data.slice(0, maxCards);
  while (data.length > 6) {
    data.shift();
  }

  return (
    <div className="card-container">
      {data.map((item, index) => (
        <div key={index} className="card">
          {/* Card Header */}
          <div className="card-header">
            <span className="counter-box">{index + 1}/{data.length}</span> 
            <h2> Comments replied </h2>
            <button className="circle-button">
              <img src="/Assets/reload.png" className="reload" />
            </button>
          </div>

          {/* Card Content */}
          <div className="card-content">
            {/* Image on the left */}
            <img src={item.imageUrl} alt="Post" className="card-image" />

            {/* Text and Button on the right */}
            <div className="card-text">
              <div>
                <h4>Post Date:</h4>
                <p className="card-date">{item.date}</p>
              </div>
              <button className="card-button">Review {data.length} Draft Replies ></button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
