import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './OnBoarding.css';

function OnBoarding() {
  const [screen, setScreen] = useState(1);
  const [fade, setFade] = useState(true);

  const renderProgressBar = () => {
    return (
      <div className="progress-bar">
        <img src="/Assets/onboarding_1.png" alt="Step 1" className={screen === 1 ? 'active' : ''} />
        <img src="/Assets/onboarding_2.png" alt="Step 2" className={screen === 2 ? 'active' : ''} />
        <img src="/Assets/onboarding_3.png" alt="Step 3" className={screen === 3 ? 'active' : ''} />
        <img src="/Assets/onboarding_4.png" alt="Step 4" className={screen === 4 ? 'active' : ''} />
      </div>
    );
  };

  const nextScreen = () => {
    setFade(false); // Trigger fade out

    setTimeout(() => {
      setScreen(screen => (screen === 4 ? 1 : screen + 1));
      setFade(true); // Trigger fade in
    }, 500); // This duration should match the CSS transition duration
  };

  useEffect(() => {
    setFade(true);
  }, []);

  const renderScreen = () => {
    return (
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {(() => {
          switch (screen) {
            case 1:
              return (
                <>
                <h1><span style={{ color: 'white' }}>Screen</span> <span style={{ color: 'red' }}>1</span></h1>
                  <p>This is the content of the first screen.</p>
                  <button onClick={nextScreen}>Go to Next Screen</button>
                  {/* Add more content specific to Screen 1 here */}
                </>
              );
            case 2:
              return (
                <>
                  <h1>Screen 2</h1>
                  <p>This is the content of the second screen.</p>
                  <button onClick={nextScreen}>Go to Next Screen</button>
                  {/* Add more content specific to Screen 2 here */}
                </>
              );
            case 3:
              return (
                <>
                  <h1>Screen 3</h1>
                  <p>This is the content of the third screen.</p>
                  <button onClick={nextScreen}>Go to Next Screen</button>
                  {/* Add more content specific to Screen 2 here */}
                </>
              );
            case 4:
              return (
                <>
                  <h1>Screen 4</h1>
                  <p>This is the content of the fourth screen.</p>
                  <button onClick={nextScreen}>Go to Next Screen</button>
                  {/* Add more content specific to Screen 2 here */}
                </>
              );  
            default:
              return null;
          }
        })()}
      </div>
    );
  };

  return (
    <div className="onboarding">
      {renderProgressBar()}
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {renderScreen(screen)}
      </div>
      <img className='bottom-img' src="/Assets/blackFullLogo.png" alt="Superpower Logo" />
    </div>
  );
}

export default OnBoarding;
