import React, { useState } from 'react';
import './OnBoarding.css';

function OnBoarding() {
  const [currentText, setCurrentText] = useState(['🌟 Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.']);
  const [activeButton, setActiveButton] = useState(1);
  const [showNewButton, setShowNewButton] = useState(false);
  const [topImage, setTopImage] = useState('/Assets/onboarding_1.png');

  const [middleContent, setMiddleContent] = useState(<img className="middle-image" src="/Assets/intro.png" alt="Middle" />);
  const [showCenterText, setShowCenterText] = useState(true);
  const [showNewButtons, setShowNewButtons] = useState(false);


  const text1 = ['🌟 Your Personalized Engagement Hub', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text2 = ['🤖 AI-Powered Drafts, Uniquely You', <br />, <br />, 'SuperpowerDM isnt just smart; its intuitive. Our advanced language model trains to match your unique communication style, drafting replies that sound authentically you.'];
  const text3 = ['🎨 Crafted Replies, Curated by You', <br />, <br />, 'Your voice, your rules. Set the tone and let SuperpowerDM propose responses that echo your personal touch. Review, tweak, and send messages that truly represent your brand.'];
  const text4 = ['👥 Engage with Precision', <br />, <br />, 'Target the comments that align with your brands vision. Whether its a heartfelt thank you or a witty reply to a question, your drafts are ready for your magic touch.'];
  const text5 = ['🛠️ Tailor Your Engagement Strategy', <br />, <br />, 'Customize the criteria for your automated replies—whether its engaging your most loyal followers or acknowledging the early birds. Your SuperpowerDM learns and adapts to your preferences.'];
  const text6 = ['🌱 Nurture Your Digital Ecosystem', <br />, <br />, 'Your content is the seed of your community. With SuperpowerDM, you have the power to cultivate lasting relationships, one personalized message at a time.'];

  const changeText = (newText, buttonNumber) => {
    setCurrentText(newText);
    setActiveButton(buttonNumber);
    if (buttonNumber === 6) {
      setShowNewButton(true);
    }
  };

  const handleNewButtonClick = () => {
    setTopImage('/Assets/new_top_image.png'); // change to the path of the new top image
    setMiddleContent(<h1>New Header</h1>); // change to the new header
    setShowCenterText(false);
    setShowNewButtons(true);
  };

  return (
    <div className="screen">
      <img className="top-image" src={topImage} alt="Top" />
      <div className="center-content">
        {middleContent}
        {showCenterText && <p className="center-text">{currentText}</p>}
        <div className="button-group">
          {!showNewButtons && (
            <>
          <button className={activeButton === 1 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text1, 1)}></button>
          <button className={activeButton === 2 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text2, 2)}></button>
          <button className={activeButton === 3 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text3, 3)}></button>
          <button className={activeButton === 4 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text4, 4)}></button>
          <button className={activeButton === 5 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text5, 5)}></button>
          <button className={activeButton === 6 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text6, 6)}></button>
          </>
          )}
          {showNewButtons && (
            <>
              <button className="new-button-1">New Button 1</button>
              <button className="new-button-2">New Button 2</button>
            </>
          )}
        </div>
        {showNewButton && <button className="new-button" onClick={handleNewButtonClick}>Next ➔</button>}
      </div>
      <img className="bottom-image" src="/Assets/whiteFullLogo.png" alt="Bottom" />
    </div>
  );
}

export default OnBoarding;
