import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

function YourNewPage() {
  const [currentScreen, setCurrentScreen] = useState(1);
  const [fade, setFade] = useState(true);

  const changeScreen = (newScreen) => () => {
    setFade(false);

    setTimeout(() => {
      setCurrentScreen(newScreen); // Set the new screen number
      setFade(true); 
    }, 500); // Adjust the timeout duration as needed
  };

  const renderScreen = () => {
    switch (currentScreen) { // Make sure to use currentScreen instead of screen
      case 1:
        return <div className='content'> 
        <p> This is a test that is a test 1 </p> 
        </div>;
      case 2:
        return <div className='content'> 
        <p> This is a test that is a test 2 </p>
        </div>;
      case 3:
        return <div className='content'> 
        <p> This is a test that is a test 3 </p> 
        </div>;
      case 4:
        return <div className='content'> 
        {/* Content for Screen 4 */} 
        </div>;
      case 5:
        return <div className='content'> 
        {/* Content for Screen 5 */} 
        </div>;
      case 6:
        return <div className='content'> 
        {/* Content for Screen 6 */} \
        </div>;
      default:
        return <div>Something went wrong!</div>;
    }
  };

  return (
    <div className="your-new-page">
      <div className={`content ${fade ? 'fade-in' : 'fade-out'}`}>
        {renderScreen()}
      </div>
      {/* Example buttons for changing screens */}
      <button onClick={changeScreen(1)}>Screen 1</button>
      <button onClick={changeScreen(2)}>Screen 2</button>
      <button onClick={changeScreen(3)}>Screen 3</button>
      {/* Add more buttons as needed */}
    </div>
  );
}

export default YourNewPage;
