import React, { useState } from 'react';
import './OnBoarding.css';

function OnBoarding() {
  const [currentText, setCurrentText] = useState(['Your custom dashboard is where engagement with your followers meets efficiency.', <br />, <br />, 'Monitor the pulse of your posts and steer your community interactions with ease.']);

  const text1 = ['Your custom dashboard is where engagement with your followers meets efficiency.', <br />, 'Monitor the pulse of your posts and steer your community interactions with ease.'];
  const text2 = ['New text for button 2.'];
  const text3 = ['New text for button 3.'];

  const changeText = (newText) => {
    setCurrentText(newText);
  };

  return (
    <div className="screen">
      <img className="top-image" src="/Assets/onboarding_1.png" alt="Top" />
      <div className="center-content">
        <img className="middle-image" src="/Assets/intro.png" alt="Middle" />
        <p className="center-text">{currentText}</p>
        <div className="button-group">
          <button className="active-button" onClick={() => changeText(text1)}>Button 1</button>
          <button className="inactive-button" onClick={() => changeText(text2)}>Button 2</button>
          <button className="inactive-button" onClick={() => changeText(text3)}>Button 3</button>
        </div>
      </div>
      <img className="bottom-image" src="/Assets/whiteFullLogo.png" alt="Bottom" />
    </div>
  );
}

export default OnBoarding;
