import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './OnBoarding.css';

function OnBoarding() {
  const [currentText, setCurrentText] = useState(['üåü Your Personalized Engagement Hub,', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.']);
  const [activeButton, setActiveButton] = useState(1);
  const [showNewButton, setShowNewButton] = useState(false);
  const [fadeOut, setFadeOut] = useState(false);

  const [showNewElements, setShowNewElements] = useState(false);
  const [topImage, setTopImage] = useState('/Assets/onboarding_1.png');


  const [showParagraph, setShowParagraph] = useState(false);

  const navigate = useNavigate();

  const text1 = ['üåü Your Personalized Engagement Hub', <br />, <br />, 'Step into a world where your dashboard becomes the nerve center of connection. Watch your community interactions come alive, tailored by a system that learns to speak in your voice.'];
  const text2 = ['ü§ñ AI-Powered Drafts, Uniquely You', <br />, <br />, 'SuperpowerDM isnt just smart; its intuitive. Our advanced language model trains to match your unique communication style, drafting replies that sound authentically you.'];
  const text3 = ['üé® Crafted Replies, Curated by You', <br />, <br />, 'Your voice, your rules. Set the tone and let SuperpowerDM propose responses that echo your personal touch. Review, tweak, and send messages that truly represent your brand.'];
  const text4 = ['üë• Engage with Precision', <br />, <br />, 'Target the comments that align with your brands vision. Whether its a heartfelt thank you or a witty reply to a question, your drafts are ready for your magic touch.'];
  const text5 = ['üõ†Ô∏è Tailor Your Engagement Strategy', <br />, <br />, 'Customize the criteria for your automated replies‚Äîwhether its engaging your most loyal followers or acknowledging the early birds. Your SuperpowerDM learns and adapts to your preferences.'];
  const text6 = ['üå± Nurture Your Digital Ecosystem', <br />, <br />, 'Your content is the seed of your community. With SuperpowerDM, you have the power to cultivate lasting relationships, one personalized message at a time.'];

  const changeText = (newText, buttonNumber) => {
    setCurrentText(newText);
    setActiveButton(buttonNumber);
    if (buttonNumber === 6) {
      setShowNewButton(true);
    }
  };

  const handleNewButtonClick = () => {
    setFadeOut(true);
    setTimeout(() => {
      setShowNewElements(true);
    }, 1000);
    setTopImage('/Assets/onboarding_2.png');
  };

  const handleSkipButtonClick = () => {
    setShowParagraph(true);
  };

  const handleTwitterLogin = () => {
    window.location.href = 'https://localhost:5000/login/twitter';
  };

  useEffect(() => {
    const searchParams = new URLSearchParams(window.location.search);
    if (searchParams.get('twitter_login_success') === 'true') {
      handleSuccessfulTwitterLogin();
    }
  }, []);

  return (
    <div className="screen">
      <img className="top-image" src={topImage} alt="Top" />
      <div className={`center-content ${fadeOut ? 'fade-out' : ''}`}>
        <img className="middle-image" src="/Assets/intro.png" alt="Middle" />
        <p className="center-text">{currentText}</p>
        <div className={`button-group ${fadeOut ? 'fade-out' : ''}`}>
          <button className={activeButton === 1 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text1, 1)}></button>
          <button className={activeButton === 2 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text2, 2)}></button>
          <button className={activeButton === 3 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text3, 3)}></button>
          <button className={activeButton === 4 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text4, 4)}></button>
          <button className={activeButton === 5 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text5, 5)}></button>
          <button className={activeButton === 6 ? 'active-button' : 'inactive-button'} onClick={() => changeText(text6, 6)}></button>
          {showNewButton && <button className={`new-button ${fadeOut ? 'fade-out' : ''}`} onClick={handleNewButtonClick}>Next ‚ûî</button>}
        </div>
      </div>
      {showNewElements && (
            <div className="new-elements">
              <h1>Connect X account</h1>
              <h2>(Make sure it's public!)</h2>
              <p className={`skip-warning ${showParagraph ? 'show' : ''}`}><h2 style={{fontSize: 'x-large', color: 'red'}}>Warning!</h2>Linking your X account allows SuperpowerDM to draft messages that truly sound like you - which is where the magic happens.
              <br /><br />This step is optional but it‚Äôs key for precision and authenticity in your replies and DMs.</p>
              <div className="button-container">
              <button className="skip-button" onClick={handleSkipButtonClick}>Skip step</button>
                <button className="x-button" onClick={handleTwitterLogin}>
                  <img src="/Assets/X-logo-black.png" alt="X Logo" style={{width: '20px', marginRight: '10px'}} />
                  Connect to X
                </button>
              </div>
            </div>
          )}
      <img className={`bottom-image`} src="/Assets/whiteFullLogo.png" alt="Bottom" />
    </div>
  );
}

export default OnBoarding;
