import React, { useState } from 'react';
import './Home.css'; // Update with your actual CSS file

function YourNewPage() {
  const [currentScreen, setCurrentScreen] = useState(1);
  const [fade, setFade] = useState(true);
  const [expanded, setExpanded] = useState(false);

  const changeScreen = (newScreen) => {
    setTimeout(() => {
      setCurrentScreen(newScreen); // Set the new screen number
    }, 100); // Adjust the timeout duration as needed
  };


  const renderScreen = () => {
    switch (currentScreen) { // Make sure to use currentScreen instead of screen
      case 1:
        return (
          <div className='content'>
            <p> Control Centre </p> 
                    <div class="flex-1 flex flex-col overflow-hidden">
                        <header class="flex justify-between items-center p-6">
                            <div class="flex items-center space-x-4">
                                <h1 class="text-2xl font-semibold text-gray-900">Control Centre</h1>
                                <span class="text-sm bg-red-500 text-white py-1 px-3 rounded-full">Process is OFF</span>
                            </div>
                        </header>
                        <main class="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100">
                            <div class="container mx-auto px-6 py-8">
                                <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                                    <div class="bg-white shadow-lg rounded-lg p-4">
                                        <div class="flex items-center justify-between">
                                            <h3 class="text-lg font-semibold">Comments replied</h3>
                                            <span class="text-sm font-semibold">0/23</span>
                                        </div>
                                        <div class="mt-4">
                                            <p class="text-sm text-gray-600">Post date: November 12, 12pm EST</p>
                                            <div class="mt-4">
                                                <button class="bg-green-500 text-white text-sm px-4 py-2 rounded hover:bg-green-600 focus:outline-none focus:bg-green-600">
                                                    Review 23 draft replies
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </main>
                    </div>
                </div>

        );
      case 2:
        return (
          <div className='content'>
            <p> Process Configuration </p>
          </div>
        );
        case 3:
        return (
          <div className='content'>
            <p> Settings </p>
          </div>
        );
        case 4:
        return (
          <div className='content'>
            <p> Coming Soon! </p>
          </div>
        );
      // ... and so on for the other cases
      default:
        return <div>Something went wrong!</div>;
    }
  };

  return (
    <div className="Background">
      {renderScreen()}
      <div className={`sidebar ${expanded ? 'expanded' : ''}`} 
           onMouseEnter={() => setExpanded(true)} 
           onMouseLeave={() => setExpanded(false)} >
        {expanded ? (
          <>
            <div onClick={() => changeScreen(1)}>
              <img src="/Assets/colourFullLogo.png" alt="Logo" className='side-logo' />
            </div>
            <button onClick={() => changeScreen(1)}> 
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Control Centre
            </button>
            <button onClick={() => changeScreen(2)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Process Configuration
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Campaigns (~Coming Soon!)
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
              Audiences (~Coming Soon!)
            </button>
            <button onClick={() => changeScreen(3)} className="button-settings"> 
              <img src="/Assets/SettingsIcon.png" alt="Icon" className="button-icon" />
              Settings
            </button>
          </>
        ) : (
          <>
            <div onClick={() => changeScreen(1)}>
              <img src="/Assets/redIcon.png" alt="Logo" className='side-logo-closed' />
            </div>
            <button onClick={() => changeScreen(1)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(2)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(4)}>
              <img src="/Assets/SideButton.png" alt="Icon" className="button-icon" />
            </button>
            <button onClick={() => changeScreen(3)} className="button-settings"> 
              <img src="/Assets/SettingsIcon.png" alt="Icon" className="button-icon" />
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default YourNewPage;
